     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('Budget');
    }

    public function getBudgetsForAsso($asso)
    {
      $q = $this->createQuery('q')->where('q.asso_id=?', $asso->getPrimaryKey())->andWhere('q.deleted_at IS NULL');
